    public Date getDateOfBirth() {
        return dateOfBirth;
    }

    public void setDateOfBirth(Date dateOfBirth) {
        Instant instant = Instant.ofEpochMilli(dateOfBirth.getTime());
        LocalDateTime localDateTime = LocalDateTime.ofInstant(instant, ZoneId.systemDefault());
        LocalDate localDate = localDateTime.toLocalDate();
        Period diff = Period.between(localDate, LocalDate.now());
        if (diff.getYears() > 18) {
            this.dateOfBirth = dateOfBirth;
        }  else {
            throw new RuntimeException("Below appropriate Age");
        }
    }

    public String getStatus() {
        return status;
    }

    public void setStatus(String status) {
        this.status = status;
    }

    public String getPosition() {
        return position;
    }

    public void setPosition(String position) {
        this.position = position;
    }

    @Override
    public String toString() {
        return "Employee [dateOfBirth=" + dateOfBirth + ", email=" + email + ", employeeName=" + employeeName + ", id="
                + id + ", nationalId=" + nationalId + ", phoneNumber=" + phoneNumber + ", position=" + position
                + ", status=" + status + "]";
    }

    
    
